		SELECT
		
		FROM T_USER U
		
			U.ACTIVE = '1'
			
	        	AND U.USER_ID = #{userId, jdbcType=VARCHAR}
	      	
	      	
	        	AND U.WX_ID = #{wxId, jdbcType=VARCHAR}
	      	
			
	        	AND U.USER_NAME = #{userName, jdbcType=VARCHAR}
	      	
	      	
        		AND U.USER_REAL_NAME = #{userRealName, jdbcType=VARCHAR}
      		
	      	
